-- -----------------------------------------------------
-- View `oficioive7122020`.`listaDiccionarioFormulario`
-- -----------------------------------------------------
DROP TABLE IF EXISTS `oficioive7122020`.`listaDiccionarioFormulario`;
DROP VIEW IF EXISTS `oficioive7122020`.`listaDiccionarioFormulario` ;
USE `oficioive7122020`;
CREATE  OR REPLACE VIEW `listaDiccionarioFormulario` AS
select 
tb2.idDiccionarioFormulario
,tb2.estado
,tb2.idUser
,tb2.tipoActuacion
,tb2.calidadActua
,concat(tb2.primerNombre,' ',tb2.segundoNombre,' ',tb2.primerApellido,' ',tb2.segundoApellido,' ',tb2.apellidoCasada,' ',tb2.otrosNombres ) as nombre
from (
select 
tb1.idDiccionarioFormulario
,tb1.estado
,tb1.idUser
,cm.tipoActuacion
,cm.calidadActua
,dp.primerNombre
,IF(dp.segundoNombre = 'SON','', dp.segundoNombre) as segundoNombre
,dp.primerApellido
,IF(dp.segundoApellido = 'SOA','', dp.segundoApellido) as segundoApellido
,dp.apellidoCasada
,dp.otrosNombres
from (
	select 
	dc.idDiccionarioFormulario
	,dc.estado
    ,dc.idUser
	,min(titular.idCamposMinimos) as idCamposMinimos
	from diccionarioFormulario as dc
	inner join titular on titular.idDiccionarioFormulario = dc.idDiccionarioFormulario
    where dc.estado = 'A'
	group by dc.idDiccionarioFormulario,dc.estado
)tb1
inner join camposMinimos cm on tb1.idCamposMinimos = cm.idCamposMinimos
inner join datosPersonales dp on cm.cliente = dp.idDatosPersonales)tb2;
